import React,{useState} from 'react'
import "./login.css"
import axios from "axios"
import {Link } from 'react-router-dom'
import history from "../history"
// import Homepage from './components/homepage/homepage'


const Login = () => {
    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value

        })
    }
    const login=()=>{
      const {  email, password } = user

      if( (email && password ) && true){
          axios.get("http://localhost:5000/api/auth", user)
          .then( res => {
              console.log(res)
              alert("login succesfully");
            //   <Link to="/homepage"></Link>

              history.push({pathname:"/homepage"},userema)
          })
          .catch((err)=>{
            console.log("wrongggg",err);
            //   alert("user name passwors are wrong.")
            //   .then(()=>{
            //     <Link to="/login"></Link>
            //   }
            //   )
            //   console.log(err);
          }
          )
      }
    //   alert("register again")
  }

  return (

          <div className="login">
          {console.log(user)}
            <h1>Login</h1>
            <form  method="post">
            <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
            <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
            <div className="button" onClick={login}>Login</div>
            <div>or</div>
          <Link to="/register" className="link">  <div className="buttonone">Register </div></Link>
          </form>
        </div>
  )
}

export default Login
