import React, {useState} from "react"
import "./register.css";
import axios from "axios";
import {Link } from 'react-router-dom'
import { useNavigate } from "react-router-dom";
// import Login from './components/login/login'

const Register = () => {
    const navigate = useNavigate();
    const [ user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        reEnterPassword:""
    })
    const handleChange = e => {
        const { name, value } = e.target
        // console.log(e.target)
        setUser({
            ...user,
            [name]: value
        })
    }

    const register=(e)=>{
        e.preventDefault();
        const { name, email, password, reEnterPassword } = user
        if( (name && email && password) && (password === reEnterPassword) || true){
            axios.post("http://localhost:5000/api/users", user)
            .then( res => {
                console.log(res)
                navigate("/login");
                // history.push("/login")
            })
            .catch((err)=>{
                alert("registation is not succesfully..")
                console.log(err);
            })
        } 
    }

  return (
    <div className="register">
    {console.log(user)}
    <h1>Register</h1>
    <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Enter your name"></input>
    <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    <input type="password" name="password"  value={user.password}  onChange={handleChange} placeholder="Enter your password"></input>
    <input type="password" name="reEnterPassword"  value={user.reEnterPassword}  onChange={handleChange} placeholder="Enter your re-Password" ></input>
    <Link to="/login" className="link" ><div className="button" onClick={register} >Register</div></Link>
    <div>Or</div>
    <Link to="/login" className="link"> <div className="buttonone">Login</div> </Link> 

</div>  )
}

export default Register
