    //     } else {
    //         alert("invlid input")
    //     }
    // }

    const register=async(event)=>{
        event.preventDefault();
        const{name,email,password,reEnterPassword}=user ;
        const res=fetch("http://localhost:5000/api/users",{
            method:"POST",
            headers:{
                "Content-Type":"application/json",
                withCredentials:true
            },

            body:JSON.stringify({
                name:name,email:email,password:password,reEnterPassword:reEnterPassword
            })
        }).then((res)=>{
            console.log(res)
        }).catch(()=>{
            alert("NOT Register")
            c
        }) 
        console.log(res)
        // const data=res.json();
        // if(data.status===404 || !data){
        //    window.alert("Invalid registation")
        //     console.log("Invalid registation");
        // }else{
        //     alert("registation succesfully")
        //     console.log("registation succesfully");
        //     navigate('/auth')
        // }
  }

  return (
    <div className="register">
    {console.log(user)}
    <h1>Register</h1>
    <form  method="POST" >
    <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Enter your name"></input>
    <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    <input type="password" name="password"  value={user.password}  onChange={handleChange} placeholder="Enter your password"></input>
    <input type="password" name="reEnterPassword"  value={user.reEnterPassword}  onChange={handleChange} placeholder="Enter your re-Password" ></input>

    <div className="button" onClick={register} >Register</div>
    <div>Or</div>
    <Link to="/login" className="link"> <div className="buttonone">Login</div> </Link> 
    </form>
</div>  )
}

export default Register
