import React, {useState} from "react"
import "./register.css";
import axios from "axios";
import {Link } from 'react-router-dom'


const Register = () => {
    // const history=useHistory()
    const [ user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        reEnterPassword:""
    })
    const handleChange = e => {
        const { name, value } = e.target
        // console.log(e.target)
        setUser({
            ...user,
            [name]: value
        })
    }

    const register=()=>{
        const { name, email, password, reEnterPassword } = user
        if( name && email && password && (password === reEnterPassword)){

            axios.post("localhost:5000/register", user)
            .then( res => {
                console.log(res)
            //     alert(res.data.message)
            //     history.push("/login")
            })
        } else {
            alert("invlid input")
        }

    }

  return (
    <div className="register">
    {console.log(user)}
    <h1>Register</h1>
    <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Enter your name"></input>
    <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    <input type="password" name="password"  value={user.password}  onChange={handleChange} placeholder="Enter your password"></input>
    <input type="password" name="reEnterPassword"  value={user.reEnterPassword}  onChange={handleChange} placeholder="Enter your re-Password" ></input>
    <div className="button" onClick={register} >Register</div>
    <div>or</div>
    <Link to="/login" className="link"> <div className="buttonone">Logout</div> </Link> 

</div>  )
}

export default Register
