import "./register.css";
import axios from "axios";
import {Link } from 'react-router-dom'


const Register = () => {
    const [ user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        reEnterPassword:""
    })
    const handleChange = e => {
        const { name, value } = e.target
        // console.log(e.target)
        setUser({
            ...user,
            [name]: value
        })
    }

    // const register=()=>{
